"""
Given an array nums which consists of non-negative integers and an integer m, you can split the array into m non-empty continuous subarrays.

Write an algorithm to minimize the largest sum among these m subarrays.

Example 1:

Input: nums = [7,2,5,10,8], m = 2
Output: 18
Explanation:
There are four ways to split nums into two subarrays.
The best way is to split it into [7,2,5] and [10,8],
where the largest sum among the two subarrays is only 18.

Example 2:

Input: nums = [1,2,3,4,5], m = 2
Output: 9

Example 3:

Input: nums = [1,4,4], m = 3
Output: 4

Constraints:

	1 <= nums.length <= 1000
	0 <= nums[i] <= 106
	1 <= m <= min(50, nums.length)
"""
import random
import itertools
import sys
from functools import lru_cache
from typing import List
import pytest


class Solution:
    def splitArray(self, nums: List[int], m: int) -> int:
        """10/18/2020 18:42"""
        cumsum = [0]
        for n in nums:
            cumsum.append(cumsum[-1] + n)

        def sum_between(i, j):
            return cumsum[j+1] - cumsum[i]

        def binsearch(i, j, n):
            _i = i
            ret = float('inf')
            while i <= j:
                k = (i+j) >> 1
                a = sum_between(_i, k)
                b = rec(k+1, n-1)
                ret = min(ret, max(a, b))

                if a < b:
                    i = k+1
                elif a >= b:
                    j = k-1
            return ret

        @lru_cache(None)
        def rec(i, n):
            if n == 1:
                return sum(nums[i:])
            if len(nums) - i == n:
                return max(nums[i:])
            return  binsearch(i, len(nums)-(n-1)-1, n)
        return rec(0, m)

    def splitArray(self, nums: List[int], m: int) -> int:
        """10/18/2020 19:09"""
        def is_possible(k):
            c = 0
            s = 0
            for n in nums:
                s += n
                if s > k:
                    s = n
                    c += 1
                    if c >= m:
                        return False
            return True

        j = sum(nums)
        i = max(nums)

        while i < j:
            k = (i+j) >> 1
            if is_possible(k):
                j = k
            else:
                i = k+1
        return i

    def splitArray(self, nums: List[int], m: int) -> int:
        """
        Greedy doesn't work.
        """
        cumsum = [0] + list(itertools.accumulate(nums))

        @lru_cache(None)
        def split(i, n):
            if i >= len(nums):
                return 0
            if n == 1:
                return cumsum[-1] - cumsum[i]
            v = (cumsum[-1] - cumsum[i]) / n
            s = 0
            while s < v and i < len(nums):
                s += nums[i]
                i += 1
            ret = max(s, split(i, n-1))
            if s > v:
                i -= 1
                s -= nums[i]
                ret = min(ret, max(s, split(i, n-1)))
            return ret

        return split(0, m)

    def splitArray(self, nums: List[int], m: int) -> int:
        """
        Binary search
        Time complexity: O(nlogm)
        Space complexity: O(1)
        """
        def is_possible(k):
            cnt = 0
            s = 0
            for n in nums:
                if s+n > k:
                    cnt += 1
                    s = 0
                s += n
            return cnt+1 <= m

        l, r = max(nums), sum(nums)
        while l <= r:
            k = l + (r-l)//2
            if is_possible(k):
                r = k-1
            else:
                l = k+1
        return r+1


